con:config.script:
// Get the current time, as a time stamp.
java.util.Calendar cal = java.util.Calendar.getInstance();
java.util.Date dateToday = cal.getTime();

// Create a format.
java.text.SimpleDateFormat sdf = new java.text.SimpleDateFormat("dd/MM/yyyy h:mm:ss aaa");
sdf.setTimeZone(java.util.TimeZone.getTimeZone("GMT"));

// Convert time to our format.
java.lang.String dateString = ((java.text.DateFormat)sdf).format(dateToday);

def targetStep = testRunner.testCase.getTestStepByName( "Properties" );

// assign timeStamp property
targetStep.setPropertyValue( "timeStamp", dateString);

//***********************************************************************************************
// set startDate Go forward 1 hour
//***********************************************************************************************
cal.add(Calendar.HOUR_OF_DAY, 1);
java.util.Date dateLater = cal.getTime();
dateString = ((java.text.DateFormat)sdf).format(dateLater);
targetStep.setPropertyValue( "startDate", dateString);

cal.add(Calendar.HOUR_OF_DAY, 1);

//***********************************************************************************************
// set endDate Go forward 36 hours
//***********************************************************************************************
cal.add(Calendar.HOUR_OF_DAY, 36);
java.util.Date dateHours = cal.getTime();
dateString = ((java.text.DateFormat)sdf).format(dateHours);
targetStep.setPropertyValue( "endDate", dateString);


//***********************************************************************************************
// Go back a day
//***********************************************************************************************
cal.add(Calendar.DAY_OF_YEAR, -4);
java.util.Date dateYesterday = cal.getTime();

dateString = ((java.text.DateFormat)sdf).format(dateYesterday);
targetStep.setPropertyValue( "yesterday", dateString);

