def ts = testCase.getTestStepsOfType(com.eviware.soapui.impl.wsdl.teststeps.WsdlTestRequestStep.class)
	ts.each
	{
		if(it.getAssertionByName("Match content of [ns:status]"))
		log.info it.name + "-->" + "Match content of [ns:status]"
		if(it.getAssertionByName("XPath Match"))
		log.info it.name + "-->" + "XPath Match"
		if(it.getAssertionByName("XPath Match 1"))
		log.info it.name + "-->" + "XPath Match 1"
	}

