import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestCase

def groovyutils = new com.eviware.soapui.support.GroovyUtils(context)

// Get incidentIdentifier from AddEvent
def testStepAddEvent = testRunner.testCase.getTestStepByName("AddEvent")
log.info testStepAddEvent.name
def responseAddEvent = groovyutils.getXmlHolder(testStepAddEvent.name + "#Response")

responseAddEvent.namespaces["ns"] = "http://www.xmatters.com/webservices/schema#5.0"
responseAddEvent.namespaces["ax21"]  = "http://soap.web.apex.invoqsystems.com/xsd"

def expectedValue = responseAddEvent.getNodeValue("//ns:AddEventResponse/ns:return/ax21:incidentIdentifier")

