con:soapui-project.con:beforeRunScript:
//import com.eviware.soapui.model.testsuite.TestSuite;
//5.0.7_WS_Update
//def project = testSuite.getProject();

def testSuiteList = project.getTestSuites()
int suitecount = 0
testSuiteList.each
{

	def testsuite = project.getTestSuiteAt(suitecount)
	def testCaseList = testsuite.getTestCases()
	  testCaseList.each
	  {
		def testCaseCount = testsuite.getTestCaseCount()

			for (i=0;i<testCaseCount;i++)
			{
 				def testcase = testsuite.getTestCaseAt(i)
 				if(!testcase.isDisabled())
 				{
 				def prop_ts = testcase.getTestStepByName("Properties")
 				def user = context.expand('${#Project#user}')
 				prop_ts.setPropertyValue("user",user)
 				def password = context.expand('${#Project#password}')
 				prop_ts.setPropertyValue("password",password)
 				def company = context.expand('${#Project#company}')
 				prop_ts.setPropertyValue("company",company)
 				}
			}
		}
	suitecount++
}
